mapper.select:
select t.*, s.storeName from t_pay_tran_ls t 
left join t_merchant m on t.merchantNo = m.merchantNo
left join t_store s on s.merId = m.id

	LEFT JOIN t_merchant_user u ON u.merchantNo = m.merchantNo

where 1=1

    and t.cashierNo = #{cashierNo}


    and t.merchantNo = #{merchantNo}


	and DATE_FORMAT(t.transTime,'%Y-%m-%d') between #{transTime1} and #{transTime2} 


	and t.merchantName like concat(concat('%',#{merchantName}),'%')


	and t.transNum like concat(concat('%',#{transNum}),'%')

      
	and t.channel =1

      
	and t.channel =2

      
	and t.channel =1 or t.channel = 2


	and t.status= #{status}

    order by t.transTime desc
	limit #{pageIndex},#{pageSize}

select 0 id,t.merchantName,s.storeName,t.user_order_no,t.transNum,t.trade_no,t.totalPrice,(t.totalPrice - t.transPrice) discountPrice, t.transPrice,(case when t.channel = 1 then '支付宝' when t.channel = 2 then '微信' when t.channel = 3 then '银联' when t.channel = 4 then '预存款' end) channel, (case when t.subChannel = 0 then '支付宝wap' when t.subChannel = 1 then '支付宝条码' when t.subChannel = 2 then '支付宝扫码' when t.subChannel = 3 then '微信wap' when t.subChannel = 4 then '微信条码' when t.subChannel = 5 then '微信扫码' end) subChannel, (case when t.status = 0 then '未付款' when t.status = 1 then '付款中' when t.status = 2 then '已付款' when t.status = 3 then '已退款' when t.status = 4 then '退款中' when t.status = 5 then '退款失败' when t.status = 6 then '付款失败' when t.status = 7 then '撤销' end) status, t.transTime from t_pay_tran_ls t left join t_merchant m on t.serialNo = m.merchantNo left join t_store s on s.merId = m.id where 1=1
and t.transTime between #{transTime1} and #{transTime2}
and t.merchantName like concat(concat('%',#{merchantName}),'%')
and t.transNum like concat(concat('%',#{transNum}),'%')
and t.channel =#{channel}
and t.status= #{status}

select count(id) from t_pay_tran_ls t

	LEFT JOIN t_merchant_user u ON u.merchantNo = t.merchantNo

 where 1=1

	t.merchantNo=#{merchantNo}


    and t.cashierNo = #{cashierNo}


	and DATE_FORMAT(t.transTime,'%Y-%m-%d') between #{transTime1} and #{transTime2} 


	and t.merchantName like concat(concat('%',#{merchantName}),'%')


	and t.transNum like concat(concat('%',#{transNum}),'%')

      
	and t.channel =1

      
	and t.channel =2

      
	and t.channel =1 or t.channel = 2


	and t.status= #{status}

select * from( select a.transTime,a.channel, CAST(sum(case when a.channel = '支付宝' then a.count_trans end) as SIGNED) as alipayCountTrans, CAST(sum(case when a.channel = '微信' then a.count_trans end) as SIGNED) as wechatCountTrans from( select date_format(t.transTime, '%H') transTime, (case when t.channel = 1 then '支付宝' when t.channel = 2 then '微信' end) channel, count(date_format(t.transTime, '%H')) count_trans from t_pay_tran_ls t where 1=1
and date_format(t.transTime, '%Y-%m-%d') = #{transTime}
and t.merchantName like concat(concat('%',#{merchantName}),'%')
group by date_format(t.transTime, '%H'),t.channel
having t.channel = #{channel}
having t.channel = 1 or t.channel=2
)a GROUP BY a.transTime,a.channel) t
